const dgram = require(['dgram'])
const client = dgram.createSocket(['udp4']);
const message = Buffer.from('/c/testing my server');

function start(){
var txt = document.getElementById("servertxt").innerHTML
txt += "This is from JavaScript"
document.getElementById("servertxt").innerHTML = txt


client.connect(1337, '192.46.219.72', (err) => {
  document.getElementById("servertxt").innerHTML = "works"
  client.send(message, (err) => {
    document.getElementById("servertxt").innerHTML = "fail"
    client.close();
  });
  document.getElementById("servertxt").innerHTML = "works"
});

}

